import React from 'react';
import FormInput from './FormInput';

class SignIn extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: ''
        }
    }

    handleChange = evt => {
        this.setState(() => ({
            email: evt.target.email.value,
            password: evt.target.passwordvalue
        }))
    }

    handleSubmit = evt => {
        evt.preventDefault();

        this.setState(() => ({
            email: '',
            password: ''
        }))
    }


    render() {
        return (
            <div className="sign-in">
                <h2>I already have an account</h2>
                <span>Sign in with your email and password</span>

                <form onSubmit={this.handleSubmit}>
                    <FormInput
                        id="email"
                        type="email" 
                        value={this.state.email} 
                        name="email"
                        required
                        handleChange={this.handleChange}
                        label="email"
                    />
                    <FormInput 
                        id="password"
                        type="password" 
                        value={this.state.password} 
                        name="password"
                        required
                        onChange={this.handleChange}
                        label="password"
                    />
                    <input type="submit" name="SIGN IN" disable={this.state.email === '' || this.state.password === ''} />"
                </form>
            </div>
        )
    }
};

export default SignIn;
