import React from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import CategoriesOverview from '../components/CategoriesOverview';
import CategoryPage from './CategoryPage';
import WithSpinner from '../components/WithSpinner';
import { firestore, convertCollectionsSnapshotToMap } from '../firebase/firebase.utils';
import { fetchCollectionsAsync } from '../redux/actions/shop';

const CategoriesOverviewWithSpinner = WithSpinner(CategoriesOverview);
const CategoryPageWithSpinner = WithSpinner(CategoryPage);

class ShopPage extends React.Component {
    // component state.
    state = {
        loading: true
    };

    unsubscribeFromSnapshot = null;
    componentDidMount() {
        const { fetchCollectionsAsync } = this.props;
        // Dispatch fetchCollectionsAsync asynchronous action creator to fetch shop collections.
        fetchCollectionsAsync();
    }


    render() {
        const { match, isFetched } = this.props;
        return (
            <div className="shop-page">
                <Route exact path={`${match.path}`} render={(props) => <CategoriesOverviewWithSpinner  isLoading={isFetched} {...props} />} />
                <Route path={`${match.path}/:categoryId`} render={(props) => <CategoryPageWithSpinner isLoading={isFetched} {...props} />} />
            </div>
        )
    }
};

const mapStateToProps = ({ shop }, ownProps) => ({
    isFetched: shop.isFetched,
    match: ownProps.match
});

const mapDispatchToProps = ({
    fetchCollectionsAsync
});


export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
