import { ADD_ITEM } from '../actions/cart';

const INITIAL_STATE = {
    hidden: true,
    items: []
};

const cartReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case ADD_ITEM:
            return {
                ...state,
                items: [...state.items, action.item]
            }
        default:
            return state;
    }
};

export default cartReducer;
